import 'bootstrap/dist/css/bootstrap.min.css';
import {Card, Carousel} from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function Home({results}){
  const navigate = useNavigate();
console.log(results);

function handleClickFilm(item){
  navigate(`/detail/${item.id}`)
}
function handleClickSerie(item){
    navigate(`/detailSerie/${item.id}`)
}

return (
    <div className="d-flex flex-wrap justify-content-center m-2 pb-2 ">
  <Carousel className='d-flex' style={{ width: '80%', margin: 'auto', maxHeight: '500px' }}>
    {results.map((item) => (
        
    <Carousel.Item key={item.id}>
      <Card className='m-3'  onClick={() =>item.media_type=="movie"? handleClickFilm(item):handleClickSerie(item)}>
        <img
          style={{ width: "35rem" }}
          src={`https://image.tmdb.org/t/p/w1280/${item.poster_path}`}
          alt={item.title}
        />
      </Card>
      </Carousel.Item>
    ))}
  </Carousel></div>
);
}

export default Home;
