import {
    fetchPopfilm,
    rechercheFilm,
    pagePrevi,
    pageSuiva,
    rechercheTvs,
    rechercheTves,
    FETCH_FILM_DETAILS, // Assure-toi que FETCH_FILM_DETAILS est bien importé
    FETCH_SERIE_DETAILS
  } from "./typeaction";
  

  const etatInitial = {
    filmDetail: [],
    film: [],
    recherche: '',
    page: 1,
    totalPage: 1,
    tv: [],
};

const reducer = (state = etatInitial, action) => {
    switch (action.type) {
        case FETCH_FILM_DETAILS: // Utilise FETCH_FILM_DETAILS pour le détail d'un film
        return {
          ...state,
          filmDetail: action.payload, // Assure-toi que la propriété film est correctement mise à jour
        };
        case fetchPopfilm:
            return {
                ...state,
                film: action.payload.film,
                page: action.payload.page,
                recherche: '',
                totalPage: action.payload.totalPage,
            };
        case rechercheFilm:
            return {
                ...state,
                film: action.payload.film,
                recherche: action.payload.recherche,
                page: action.payload.page,
                totalPage: action.payload.totalPage,
            };
        case rechercheTvs:
            return {
                ...state,
                tv: action.payload.tv,
                page: action.payload.page,
                totalPage: action.payload.totalPage,
            };
        case FETCH_SERIE_DETAILS:
            return {
                ...state,
                filmDetail: action.payload, // Assure-toi que la propriété film est correctement mise à jour
              };
            
        case rechercheTves:
            return {
                ...state,
                tv: action.payload.tv,
                page: action.payload.page,
                totalPage: action.payload.totalPage,
            };
        case pageSuiva:
            return {
                ...state,
                page: state.page + 1,
            };
        case pagePrevi:
            return {
                ...state,
                page: state.page - 1,
            };
        default:
            return state;
    }
};


  
  export default reducer;
