import React from "react";
import { useNavigate } from "react-router-dom";
import { rechercheFilms } from "./action";
import 'bootstrap/dist/css/'

const Footer = ({recherche, dispatch, setIsButtonChecked, handleHome, handleSearchTv, handleSearchFilm}) => {
  const navigate = useNavigate();
function handleclickHome(){
  navigate('/')
}
function handleclick(){
  navigate('/film')
}
function handleclickSerie(){
  navigate('/serie')
}    
  return (
    <>
       <input
        type="text"
        value={recherche}
        placeholder="...recherche"
        onChange={(event) => dispatch(rechercheFilms(event.target.value, 1))}
      />
     <button
        onClick={() => {
          setIsButtonChecked(0);
          handleHome();
          handleclickHome();
        }}
      >
        Home
      </button>
      <button
        onClick={() => {
          handleclickSerie();
          setIsButtonChecked(1);
          handleSearchTv();
          
        }}
      >
        SÃ©rie
      </button>
      
      <button
        onClick={() => {
          handleclick();
          setIsButtonChecked(2);
          handleSearchFilm();

        }}
      >
        film
      </button>
      
    </>
  );
};

export default Footer;
