import 'bootstrap/dist/css/bootstrap.min.css';
import {Card, Carousel} from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function Home({results}){
  const navigate = useNavigate();
console.log(results);

function handleClickFilm(item){
  navigate(`/detail/${item.id}`)
}
function handleClickSerie(item){
    navigate(`/detailSerie/${item.id}`)
}

return (
    <Carousel 
    fade
    style={{ width: '80%', margin: 'auto', maxHeight: '500px' }}>
        {results.map((movie) => (
            <Carousel.Item key={movie.id} interval={10000}>
                <img
                    className="d-block w-100"
                    src={`https://image.tmdb.org/t/p/w1280/${movie.poster_path}`}
                    alt={movie.title}
                    style={{ maxHeight: '400px', objectFit: 'cover', filter: 'brightness(0.5)' }} // Ajout de styles pour ajuster la taille de l'image
                />
                <Carousel.Caption>
                    <h3>{movie.title}</h3>
                    <p>{movie.overview}</p>
                </Carousel.Caption>
            </Carousel.Item>
        ))}
    </Carousel>
);
}

export default Home;
