import {
    fetchPopfilm,
    rechercheFilm,
    pagePrevi,
    pageSuiva,
    rechercheTvs,
    rechercheTves,
    FETCH_FILM_DETAILS // Assure-toi que FETCH_FILM_DETAILS est bien importé
  } from "./typeaction";
  
  const etatInitial = {
    film: [],
    recherche: '',
    page: 1,
    totalPage: 1,
    tv: [],
  };
  
  const reducer = (state = etatInitial, action) => {
    switch (action.type) {
      case FETCH_FILM_DETAILS: // Utilise FETCH_FILM_DETAILS pour le détail d'un film
        return {
          ...state,
          film: action.payload, // Assure-toi que la propriété film est correctement mise à jour
        };
      // Les autres cas restent inchangés
      default:
        return state;
    }
  };
  
  export default reducer;
