import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react";
import { Card, Modal } from "react-bootstrap";
import { useSelector} from "react-redux";
import { rechercheFilmsModal } from "./action";


const Footer = ({isButtonChecked ,recherche, dispatch, setIsButtonChecked, handleHome, handleSearchTv, handleSearchFilm}) => {
  const [results, setResults] = useState([]);
  const film = useSelector((state) => state.reducer.filmModale);
  const tvs = useSelector((state) => state.reducer.tvModale);
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const page = useSelector((state)=>state.reducer.page)
  useEffect(() => {
    if (recherche) {
      dispatch(rechercheFilmModale(page));
      dispatch(rechercheTvModale(page));
    } 
  }, [recherche, page]);
  useEffect(() => {
    setResults([...film, ...tvs]);
  }, [film, tvs]);

function handleclickHome(){
  navigate('/')
}
function handleclick(){
  navigate('/film')
}
function handleclickSerie(){
  navigate('/serie')
}    
const handleRecherche = () => {
  // Fonction pour afficher les résultats de la recherche dans le modal
  setShowModal(true);
};

function handleClickFilm(item){
  navigate(`/detail/${item.id}`)
}
function handleClickSerie(item){
    navigate(`/detailSerie/${item.id}`)
}
  return (
  <>
  <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container">
      <button
  className={`btn btn-primary ${isButtonChecked === 0 ? 'active' : ''}`}
  onClick={() => {
    setIsButtonChecked(0);
    handleclickHome();
    handleHome();
  }}
>
  <i className="bi bi-house-fill"></i> Home
</button>
        
        <button
          className={`btn ${isButtonChecked === 1 ? 'active' : ''}`}
          onClick={() => {setIsButtonChecked(1);
                          handleclickSerie();
                          handleSearchTv();}}
        >
          Série
        </button>
        <button
          className={`btn ${isButtonChecked === 2 ? 'active' : ''}`}
          onClick={() => {setIsButtonChecked(2);
                          handleclick();
                          handleSearchFilm();}}
        >
          Film
        </button>
        <button
            className="btn btn-primary"
            onClick={handleRecherche}
          >
            Rechercher
          </button>
       
      </div>
    </nav>
    <Modal dialogClassName="" show={showModal} onHide={() => setShowModal(false)}>
    <Modal.Header closeButton className="flex-grow-1 mx-3 d-flex flex-column">
          <input
            type="text"
            className="form-control"
            value={recherche}
            placeholder="Recherche..."
            onChange={(event)=>dispatch(rechercheFilmModale(event.target.value))}
          />
      <Modal.Title>Résultats de la recherche pour "{recherche}"</Modal.Title>
    </Modal.Header>
    <Modal.Body className="d-flex flex-wrap">
    {results.map((item) => (
      <Card key={item.id} onClick={() =>item.media_type === "movie" ?  handleClickFilm(item) : handleClickSerie(item)}>
        <img className="img-fluid"
          style={{ width: "15rem" }}
          src={`https://image.tmdb.org/t/p/w1280/${item.poster_path}`}
          alt={item.title}
        />
      </Card>
    ))}
    </Modal.Body>
    <Modal.Footer>
      <button variant="secondary" onClick={() => setShowModal(false)}>
        Fermer
      </button>
    </Modal.Footer>
  </Modal>
  </>
  );
};

export default Footer;
