import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import {Card, Carousel, Col} from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function Home({results}){
    const tabSize = 5; 
    const [index, setIndex] = useState(0);

  const navigate = useNavigate();
console.log(results);

function handleClickFilm(item){
  navigate(`/detail/${item.id}`)
}
function handleClickSerie(item){
    navigate(`/detailSerie/${item.id}`)
}
        
return (
            <>
                <Carousel 
                    interval={null}
                    style={{ width: '100%', margin: 'auto', maxHeight: '500px' }} 
                    activeIndex={index} 
                    onSelect={(selectedIndex) => setIndex(selectedIndex)}
                    prevLabel="Previous" 
                    nextLabel="Next"
                    indicators={false}
                >
                    {Array.from({ length: Math.ceil(results.length / tabSize) }).map((e, slideIndex) => (
                        <Carousel.Item key={slideIndex} slide={false} >
                            <div className='d-flex'>
                                {results.slice(slideIndex * tabSize, (slideIndex + 1) * tabSize).map((movie) => (
                                    <Col key={movie.id} className='p-2'>
                                        <Card movie={movie} />
                                    </Col>
                                ))}
                            </div>
                        </Carousel.Item>
                    ))}
                </Carousel>
            </>
        );
    };



export default Home;
