import React from "react";
import { useNavigate } from "react-router-dom";
import { rechercheFilms } from "./action";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react";

const Footer = ({isButtonChecked ,recherche, dispatch, setIsButtonChecked, handleHome, handleSearchTv, handleSearchFilm}) => {

  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);

function handleclickHome(){
  navigate('/')
}
function handleclick(){
  navigate('/film')
}
function handleclickSerie(){
  navigate('/serie')
}    
  return (
  <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container">
      <button
  className={`btn btn-primary ${isButtonChecked === 0 ? 'active' : ''}`}
  onClick={() => {
    setIsButtonChecked(0);
    handleclickHome();
    handleHome();
  }}
>
  <i className="bi bi-house-fill"></i> Home
</button>
        
        <button
          className={`btn ${isButtonChecked === 1 ? 'active' : ''}`}
          onClick={() => {setIsButtonChecked(1);
                          handleclickSerie();
                          handleSearchTv();}}
        >
          SÃ©rie
        </button>
        <button
          className={`btn ${isButtonChecked === 2 ? 'active' : ''}`}
          onClick={() => {setIsButtonChecked(2);
                          handleclick();
                          handleSearchFilm();}}
        >
          Film
        </button>
        <button
            className="btn btn-primary"
            onClick={handleRecherche}
          >
            Rechercher
          </button>
        <div className="flex-grow-1 mx-3">
          <input
            type="text"
            className="form-control"
            value={recherche}
            placeholder="Recherche..."
            onChange={(event) => dispatch(rechercheFilms(event.target.value, 1))}
          />
        </div>
       
      </div>
    </nav>
    
  );
};

export default Footer;
