import React from "react";
import { useNavigate } from "react-router-dom";
import { rechercheFilms } from "./action";
import 'bootstrap/dist/css/bootstrap.min.css'

const Footer = ({isButtonChecked ,recherche, dispatch, setIsButtonChecked, handleHome, handleSearchTv, handleSearchFilm}) => {
  const navigate = useNavigate();
function handleclickHome(){
  navigate('/')
}
function handleclick(){
  navigate('/film')
}
function handleclickSerie(){
  navigate('/serie')
}    
  return (
  <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container">
        <button
          className={`navbar-brand btn ${isButtonChecked === 0 ? 'active' : ''}`}
          onClick={() => {setIsButtonChecked(0);
                          handleclickHome();}}
          
        >
          Home
        </button>
        <button
          className={`btn ${isButtonChecked === 1 ? 'active' : ''}`}
          onClick={() => {setIsButtonChecked(1);
                          handleclickSerie();}}
        >
          SÃ©rie
        </button>
        <button
          className={`btn ${isButtonChecked === 2 ? 'active' : ''}`}
          onClick={() => {setIsButtonChecked(2)}}
        >
          Film
        </button>
        <div className="flex-grow-1 mx-3">
          <input
            type="text"
            className="form-control"
            value={recherche}
            placeholder="Recherche..."
            onChange={(event) => dispatch(rechercheFilms(event.target.value, 1))}
          />
        </div>
        <button
          className="btn btn-primary"
          onClick={() => {
            setIsButtonChecked(0);
            handleHome();
          }}
        >
          <i className="bi bi-house-fill"></i> Home
        </button>
      </div>
    </nav>
  );
};

export default Footer;
