import 'bootstrap/dist/css/bootstrap.min.css';
import {Card, Carousel} from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function Home({results}){
  const navigate = useNavigate();
console.log(results);

function handleClickFilm(item){
  navigate(`/detail/${item.id}`)
}
function handleClickSerie(item){
    navigate(`/detailSerie/${item.id}`)
}

return (
    <Carousel className='d-flex flex-wrap justify-content-center'>
    {results.map((item, index) => (
      index % 5 === 0 && (
        <Carousel.Item key={index}>
          <div className="d-flex">
            {results.slice(index, index + 5).map((film, filmIndex) => (
              <Card className='m-3' key={filmIndex} onClick={() => handleClickFilm(film)}>
                <img
                  style={{ width: "35rem" }}
                  src={`https://image.tmdb.org/t/p/w1280/${film.poster_path}`}
                  alt={film.title}
                />
              </Card>
            ))}
          </div>
        </Carousel.Item>
      )
    ))}
  </Carousel>
  
);
}

export default Home;
