import 'bootstrap/dist/css/bootstrap.min.css';
import {Card, Carousel} from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function Home({results}){
  const navigate = useNavigate();
console.log(results);

function handleClickFilm(item){
  navigate(`/detail/${item.id}`)
}
function handleClickSerie(item){
    navigate(`/detailSerie/${item.id}`)
}

return (
  <Carousel className='d-flex flex-wrap justify-content-center'>
    {results.map((item) => (
    <Carousel.Item>
      <Card className='m-3' key={item.id} onClick={() =>item.media_type=="movie"? handleClickFilm(item):handleClickSerie(item)}>
        <img
          style={{ width: "35rem" }}
          src={`https://image.tmdb.org/t/p/w1280/${item.poster_path}`}
          alt={item.title}
        />
      </Card></Carousel.Item>
    ))}
  </Carousel>
);
}

export default Home;
